  return (
    <div className='App'>
      <div className='App-header' style={styles.container}>
        <h1 style={styles.heading}>Token Balance Checker</h1>
        <label style={styles.label}>
          Chain:
          <select style={styles.select} value={chain} onChange={(e) => setChain(e.target.value)}>
            {Object.keys(chains).map((chainName) => (
              <option key={chainName} value={chainName}>
                {chainName}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label style={styles.label}>
          Address:
          <input style={styles.input} type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
        </label>
        <br />
        <button style={styles.button} onClick={fetchBalance}>
          Fetch Balance
        </button>
        <p style={styles.result}>Native Token Balance: {balance}</p>
        <br />
        <br />
        <br />
