        <br />
        <button style={styles.button} onClick={fetchBalance2}>
          Fetch Balance 2
        </button>
        <p style={styles.result}>Percent {change} in last 24 hours is: {percentchange} % </p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    minHeight: '100vh', // Adjusted for full-page height
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: 'calc(10px + 2vmin)',
    color: 'white',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
  },
  select: {
    marginLeft: '10px',
    padding: '5px',
  },
  input: {
    marginLeft: '10px',
    padding: '5px',
  },
  button: {
    backgroundColor: '#007BFF',
    color: 'white',
    padding: '10px',
    cursor: 'pointer',
    border: 'none',
    borderRadius: '5px',
    marginTop: '10px',
  },
  result: {
    marginTop: '10px',
  },
};

export default TokenBalance;
